import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import "core-js/modules/es.array.push.js";
import axios from 'axios';
export default {
  name: 'ChatInterface',
  data() {
    return {
      llm_type: '0',
      user_id: this.$route.query.user_id,
      role_id: '1',
      flag: '',
      // 标识模型
      question: '',
      isClickable: true,
      // 是否处于可发送聊天状态
      groups: [],
      // 分组
      windows: [],
      // 窗口
      role_list: [],
      // 模型数组
      messages: [1],
      // 聊天消息
      newGroup: false,
      // 显示新建分组窗口命名
      group_name: '',
      // 新建分组名称
      group_id: '',
      // 当前选中分组的id号
      chat_window_id: '',
      // 当前选中的窗口的id号
      newGroupName: '',
      // 重命名的分组名
      newWindowName: '',
      // 重命名的窗口名
      changeGroup: false,
      // 是否显示转移分组列表
      times_all: 9,
      // 总共次数
      times_remain: 9,
      // 剩余次数
      timer: null,
      // 定时器检查整点
      contactUs: '12345678910@qq.com',
      //联系我们内容
      userMustKnow: '用户须知',
      // 用户须知内容
      isShowContact: false,
      // true展示联系我们内容
      isShowKnow: false,
      // true展示用户须知内容
      advertise_state: true,
      // false显示广告
      advertisementPicture: null,
      // 广告图片
      link: 'ttps://item.taobao.com/item.htm?spm=a21n57.1.0.0.a1e1523cXSiIwo&id=675229061216&ns=1&abbucket=15#detail',
      // 广告跳转链接
      isVip: false // true为VIP
    };
  },

  created() {
    document.title = '聊天-chataaa';
    this.getInfo();
    this.getTimes();
  },
  mounted() {
    if (this.role_list.length > 0) {
      this.llm_type = this.role_list[0].role_id;
    }
    this.timer = setInterval(this.checkOnTheHour, 1000); // 每秒检查一次是否到整点
  },

  beforeDestroy() {
    clearInterval(this.timer);
  },
  methods: {
    checkOnTheHour() {
      const now = new Date();
      if (now.getMinutes() === 0 && now.getSeconds() === 0) {
        // 整点
        this.getTimes();
      }
    },
    async getTimes() {
      const res = await axios.get('http://119.39.26.149:5000/user/get_query_times', {
        params: {
          user_id: this.user_id
        }
      });
      if (res.data.code === '400') {
        this.$message.error('获取提问次数失败');
        return;
      }
      this.times_all = res.data.all_query_times;
      this.times_remain = res.data.current_query_times;
    },
    huadong() {
      this.$nextTick(() => {
        const myDiv = this.$refs.myDiv;
        myDiv.scrollTop = myDiv.scrollHeight;
      });
    },
    async getInfo() {
      const res1 = await axios.get('http://119.39.26.149:5000/user/chat/get_all_group', {
        params: {
          user_id: this.user_id
        }
      });
      console.log(res1);
      this.groups = res1.data.data;
      const res2 = await axios.get('http://119.39.26.149:5000/user/chat/get_all_window', {
        params: {
          user_id: this.user_id
        }
      });
      console.log(res2);
      this.windows = res2.data.data;
      const res3 = await axios.get('http://119.39.26.149:5000/administrator/get_company_info');
      console.log(res3);
      this.contactUs = res3.data.data.contact_way;
      this.userMustKnow = res3.data.data.user_agreement;
      const res4 = await axios.get('http://119.39.26.149:5000/user/get_advertising', {
        params: {},
        responseType: 'blob'
      });
      if (res4.data.code === '400') {
        this.$message.error('获取图片失败');
        return;
      }
      const imgUrl = URL.createObjectURL(res4.data);
      this.advertisementPicture = imgUrl;
      const res5 = await axios.get('http://119.39.26.149:5000/user/chat/get_all_role', {
        params: {
          user_id: this.user_id
        }
      });
      this.role_list = res5.data.data;
      console.log(this.role_list[0].role_id);
      this.flag = this.role_list[0].flag;
      this.role_id = this.role_list[0].role_id;
      this.llm_type = this.role_list[0].llm_type;
      console.log(res5);
      const res6 = await axios.get('http://119.39.26.149:5000/user/get_advertise_state', {
        params: {
          user_id: this.user_id
        }
      });
      if (res6.data.code === '400') {
        this.$message.error('获取广告状态失败');
        return;
      }
      this.advertise_state = res6.data.advertise_state;
      const res7 = await axios.get('http://119.39.26.149:5000/administrator/get_url_link');
      if (res7.data.code === '400') {
        this.$message.error('获取链接错误');
        return;
      }
      this.link = res7.data.data.url;
      const res8 = await axios.get('http://119.39.26.149:5000/user/get_vip_state', {
        params: {
          user_id: this.user_id
        }
      });
      if (res8.data.code === '400') {
        this.$message.error('获取vip身份失败');
        alert('获取vip身份失败');
        return;
      }
      this.isVip = res8.data.state;
    },
    contact() {
      this.isShowContact = true;
    },
    closeContact() {
      this.isShowContact = false;
    },
    mustKnow() {
      this.isShowKnow = true;
    },
    closeMustKnow() {
      this.isShowKnow = false;
    },
    async exit() {
      const res = await axios.post('http://119.39.26.149:5000/login_system/log_out', {
        user_id: this.user_id
      });
      if (res.data.code === '400') {
        alert('退出登录失败');
        return;
      }
      this.$router.push('/login_system');
    },
    showWindows(groups, index) {
      // this.isDown = !this.isDown
      // this.isUp = !this.isUp
      if (groups[index].judge === 'true') {
        groups[index].judge = 'false';
      } else {
        groups[index].judge = 'true';
      }
    },
    changeGroupName(groups, index, group_name) {
      // 点击分组重命名
      groups[index].change_group_name1 = 'false';
      groups[index].change_group_name2 = 'true';
      this.newGroupName = group_name;
    },
    async chessRenameG() {
      // 重命名分组
      if (this.newGroupName === '') {
        alert(`名称不能为空`);
        return;
      }
      const res = await axios.post('http://119.39.26.149:5000/user/chat/rename_group', {
        group_id: this.group_id,
        group_name: this.newGroupName
      });
      this.newGroupName = '';
      if (res.data.code === '400') {
        alert('操作失败');
        return;
      }
      this.getInfo();
    },
    noRenameG(groups, index) {
      // 取消重命名分组
      groups[index].change_group_name1 = 'true';
      groups[index].change_group_name2 = 'false';
    },
    async delG(group_id) {
      // 删除分组
      const res = await axios.delete(`http://119.39.26.149:5000/user/chat/delete_group?group_id=${group_id}`);
      if (res.data.code === '400') {
        alert('操作失败');
        return;
      }
      this.getInfo();
    },
    isDelG(group_id) {
      // 确认是否删除分组
      const isConfirmed = window.confirm('删除分组后，该分组下所有窗口也会被删除，确定要删除该分组吗？');
      if (isConfirmed) {
        this.delG(group_id);
      } else {
        console.log('取消删除分组');
      }
    },
    changeWindowName(windows, index, chat_window_name) {
      // 点击窗口重命名
      windows[index].change_window_name1 = 'false';
      windows[index].change_window_name2 = 'true';
      this.newWindowName = chat_window_name;
    },
    async chessRenameW() {
      // 重命名窗口
      if (this.newWindowName === '') {
        alert(`名称不能为空`);
        return;
      }
      const res = await axios.post('http://119.39.26.149:5000/user/chat/rename_window', {
        chat_window_id: this.chat_window_id,
        chat_window_name: this.newWindowName
      });
      this.newWindowName = '';
      if (res.data.code === '400') {
        alert('操作失败');
        return;
      }
      this.getInfo();
    },
    noRenameW(windows, index) {
      // 取消重命名窗口
      windows[index].change_window_name1 = 'true';
      windows[index].change_window_name2 = 'false';
    },
    changeG(windows, index) {
      // 显示转移窗口的分组列表
      if (this.changeGroup === true) {
        this.changeGroup = false;
        return;
      }
      this.chat_window_id = windows[index].chat_window_id;
      this.changeGroup = true;
    },
    async chessChange(group_id) {
      // 确实转移
      const res = await axios.post('http://119.39.26.149:5000/user/chat/group_add_window', {
        chat_window_id: this.chat_window_id,
        group_id: group_id
      });
      if (res.data.code === '400') {
        alert('操作失败');
        return;
      }
      this.getInfo();
    },
    close() {
      // 关闭转移窗口的分组列表
      this.changeGroup = false;
    },
    async delW(chat_window_id) {
      // 删除窗口
      const res = await axios.delete(`http://119.39.26.149:5000/user/chat/delete_window?chat_window_id=${chat_window_id}`);
      if (res.data.code === '400') {
        alert('操作失败');
        return;
      }
      this.getInfo();
    },
    isDelW(chat_window_id) {
      // 确认是否删除窗口
      const isConfirmed = window.confirm('确定要删除该窗口吗？');
      if (isConfirmed) {
        this.delW(chat_window_id);
      } else {
        console.log('取消删除窗口');
      }
    },
    async displayWindow(group_id, chat_window_id) {
      // 点击窗口显示对应聊天页面及其聊天记录
      const res = await axios.get('http://119.39.26.149:5000/user/chat/get_recorder', {
        params: {
          chat_window_id: chat_window_id
        }
      });
      this.group_id = group_id;
      this.chat_window_id = chat_window_id;
      this.messages = res.data.data;
      this.huadong();
    },
    clickNewGroup() {
      // 点击新建分组
      this.newGroup = true;
    },
    async createGroup() {
      // 确定新建分组
      const res = await axios.post('http://119.39.26.149:5000/user/chat/new_group', {
        user_id: this.user_id,
        group_name: this.group_name
      });
      this.newGroup = false;
      if (res.data.code === '400') {
        alert('操作失败');
        return;
      }
      this.group_id = res.data.group_id;
      this.getInfo();
      this.group_name = '';
    },
    cancel() {
      // 取消新建分组
      this.newGroup = false;
    },
    async clickNewChat() {
      // 新建窗口
      if (this.group_id === '') {
        alert(`请选中分组后再创建窗口`);
        return;
      }
      const res = await axios.post('http://119.39.26.149:5000/user/chat/new_chat', {
        user_id: this.user_id,
        group_id: this.group_id
      });
      if (res.data.code === '401') {
        alert(`参数有误`);
      }
      if (res.data.code === '400') {
        alert(`创建失败，请重新创建`);
        return;
      }
      this.chat_window_id = res.data.chat_window_id;
      this.getInfo();
    },
    chooseGroup(group_id) {
      // 获取当前选中分组id号
      this.group_id = group_id;
    },
    chooseWindow(group_id, chat_window_id) {
      // 获取当前选中窗口id号
      this.group_id = group_id;
      this.chat_window_id = chat_window_id;
    },
    async sendQuestion() {
      // 发送问题
      if (this.question.trim() === '') {
        alert(`输入内容不能为空`);
        return;
      }
      this.isClickable = false;
      const res = await axios.post('http://119.39.26.149:5000/semantic_search/query', {
        llm_type: this.llm_type,
        user_id: this.user_id,
        chat_window_id: this.chat_window_id,
        role_id: this.role_id,
        question: this.question
      });
      this.question = '';
      if (res.data.code === '400') {
        alert('提问失败');
        return;
      }
      this.displayWindow(this.chat_window_id);
      this.isClickable = true;
      this.times_remain = res.data.current_query_times;
      if (res.data.code === '403') {
        alert(`提问次数已达上限`);
      }
      if (res.data.code === '400') {
        alert(`使用模型出现问题`);
      }
    },
    closeAdv() {
      this.advertise_state = true;
    },
    manageRoles() {
      if (this.isVip) {
        alert('Vip');
      } else {
        alert('该功能为VIP用户专享，快去升级会员吧！');
      }
    },
    createRoles() {
      if (this.isVip) {
        this.$router.push({
          path: '/createRoles',
          query: {
            user_id: this.user_id
          }
        });
      } else {
        alert('该功能为VIP用户专享，快去升级会员吧！');
      }
    },
    setting() {
      this.$router.push({
        path: '/setting',
        query: {
          user_id: this.user_id
        }
      });
    }
  },
  watch: {
    flag(newVal) {
      const role = this.role_list.find(item => item.flag === newVal);
      if (role) {
        this.role_id = role.role_id;
        this.llm_type = role.llm_type;
      }
    }
  }
};
